				$key3 = KlasyPeer::getPrimaryKeyHashFromRow($row, $startcol3);
				if ($key3 !== null) {
					$obj3 = KlasyPeer::getInstanceFromPool($key3);
					if (!$obj3) {
	
						$cls = KlasyPeer::getOMClass(false);

					$obj3 = new $cls();
					$obj3->hydrate($row, $startcol3);
					KlasyPeer::addInstanceToPool($obj3, $key3);
				} // if $obj3 already loaded

				// Add the $obj1 (Lekcje) to the collection in $obj3 (Klasy)
				$obj3->addLekcje($obj1);

			} // if joined row is not null

				// Add objects for joined Semestr rows

				$key4 = SemestrPeer::getPrimaryKeyHashFromRow($row, $startcol4);
				if ($key4 !== null) {
					$obj4 = SemestrPeer::getInstanceFromPool($key4);
					if (!$obj4) {
	
						$cls = SemestrPeer::getOMClass(false);

					$obj4 = new $cls();
					$obj4->hydrate($row, $startcol4);
					SemestrPeer::addInstanceToPool($obj4, $key4);
				} // if $obj4 already loaded

				// Add the $obj1 (Lekcje) to the collection in $obj4 (Semestr)
				$obj4->addLekcje($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}

