		if ($this->isColumnModified(PlanlekcjiPeer::SEMESTR_ID)) $criteria->add(PlanlekcjiPeer::SEMESTR_ID, $this->semestr_id);
		if ($this->isColumnModified(PlanlekcjiPeer::KLASA_ID)) $criteria->add(PlanlekcjiPeer::KLASA_ID, $this->klasa_id);
		if ($this->isColumnModified(PlanlekcjiPeer::NAUCZYCIEL_ID)) $criteria->add(PlanlekcjiPeer::NAUCZYCIEL_ID, $this->nauczyciel_id);
		if ($this->isColumnModified(PlanlekcjiPeer::PRZEDMIOT_ID)) $criteria->add(PlanlekcjiPeer::PRZEDMIOT_ID, $this->przedmiot_id);
		if ($this->isColumnModified(PlanlekcjiPeer::CZASSTART)) $criteria->add(PlanlekcjiPeer::CZASSTART, $this->czasstart);
		if ($this->isColumnModified(PlanlekcjiPeer::CZASSTOP)) $criteria->add(PlanlekcjiPeer::CZASSTOP, $this->czasstop);
