		if ($this->isColumnModified(PlanlekcjiPeer::GODZINALEKCYJNA)) $criteria->add(PlanlekcjiPeer::GODZINALEKCYJNA, $this->godzinalekcyjna);
		if ($this->isColumnModified(PlanlekcjiPeer::GRUPA_ID)) $criteria->add(PlanlekcjiPeer::GRUPA_ID, $this->grupa_id);
		if ($this->isColumnModified(PlanlekcjiPeer::OBOWIAZKOWA)) $criteria->add(PlanlekcjiPeer::OBOWIAZKOWA, $this->obowiazkowa);

